import React, {Component} from 'react';
import styled from './style.module.css';

class CronometroDigital extends Component{
    constructor(props){
        super(props);
        this.state = {
            hora: '00 : 00: 00',
            data: '',
        }
    }
    componentDidMount(){
        setInterval(()=>{
            this.setState({hora: new Date().toLocaleTimeString()})
        },1000);
    }
    data(){
        this.state({data: new Date().toLocaleDateString()});
    }


    render(){
        return(
            <div className={styled.container}>
                <img src={require('../ASSETS/relogio.png')} alt={'img-relogio'} className={styled.img} ></img>
                <h1>{this.state.hora}</h1>

            </div>
        );
    }
}
export default CronometroDigital;
