import React, {Component} from "react";
import styled from './style.module.css';

class Cronometragem extends Component{
    constructor(props){
        super(props);
        this.state = {
            segundos: 0,
            minutos: null,
            botao: 'Começar'
        };
        this.timer = null;
        this.comecar = this.comecar.bind(this);
        this.zerar = this.zerar.bind(this);
    }
    comecar(){
        let state = this.state;

        if (this.timer !== null){
            clearInterval(this.timer);
            this.timer = null;
            state.botao = 'Começar'
        }else{

                this.timer = setInterval(()=>{
                    let state = this.state;
                    state.segundos += 1;
                    this.setState(state);
                },10);
                state.botao = 'pausar';
            }


        this.setState(state);
    }


    zerar(){
        if(this.timer !== null){
            clearInterval(this.timer);
            this.timer = null;
        }
        let state = this.state;
        state.segundos = 0;
        state.botao = 'Começar'
        this.setState(state);
    }
    render(){
        return(
            <div className={styled.container}>
                <a className={styled.tempo}>{this.state.segundos}{this.state.segundos.toFixed(0)}</a>
                <a href=""></a>
                <div className={styled.areaBtn}>
                    <button className={styled.botao} onClick={this.comecar}>{this.state.botao}</button>
                    <button className={styled.botao} onClick={this.zerar}>Limpar</button>


                </div>

            </div>
        );
    }
}
export default Cronometragem;
